<?php

/**
 * Admin Menu
 */
class ND_Forms_Menu {

	/**
	 * Kick-in the class
	 */
	public function __construct() {
		add_action( 'admin_menu', array( $this, 'admin_menu' ) );
	}

	/**
	 * Add menu items
	 *
	 * @return void
	 */
	public function admin_menu() {

		$icon_base64 = '';
		$icon_data_uri = 'data:image/svg+xml;base64,' . $icon_base64;

		/** Top Menu **/
		$hook = add_menu_page(
			'ND-Forms',
			'ND-Forms',
			'edit_pages',
			'nd-forms-lulags',
			array( $this, 'nd_forms_lulags_page' ),
			$icon_data_uri,
			26
		);

		add_submenu_page(
			'nd-forms-lulags',
			'L&L/AGs',
			'L&L/AGs',
			'edit_pages',
			'nd-forms-lulags',
			array( $this, 'nd_forms_lulags_page' ),
			10
		);

		add_submenu_page(
			'nd-forms-lulags',
			'Dorfplatzangebote',
			'Dorfplatzangebote',
			'edit_pages',
			'nd-forms-dorfplatz',
			array( $this, 'nd_forms_dpas_page' ),
			20
		);

		add_submenu_page(
			'nd-forms-lulags',
			'Mittagsbuden',
			'Mittagsbuden',
			'edit_pages',
			'nd-forms-buden',
			array( $this, 'nd_forms_mbs_page' ),
			30
		);
	}

	/**
	 * Handles the plugin page
	 *
	 * @return void
	 */
	public function nd_forms_main_page() {
		include dirname(__FILE__) . '/views/main.php';
	}

	/**
	 * Handles the plugin page
	 *
	 * @return void
	 */
	public function nd_forms_lulags_page() {
		include dirname(__FILE__) . '/views/lulags/main-view.php';
	}

	/**
	 * Handles the plugin page
	 *
	 * @return void
	 */
	public function nd_forms_dpas_page() {
		include dirname(__FILE__) . '/views/dpas/main-view.php';
	}

	/**
	 * Handles the plugin page
	 *
	 * @return void
	 */
	public function nd_forms_mbs_page() {
		include dirname(__FILE__) . '/views/mbs/main-view.php';
	}
}